import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, } from 'react-native';
import { useFonts, Roboto_400Regular, Roboto_500Medium } from '@expo-google-fonts/roboto';

import styles from './styles';
import api from '../../services/api';

const Flatlist = () => {
    const [employees, setEmployees] = useState([])

    useEffect(() => {
        api.get("/employees").then((response) => {
            console.log(response.data);
            setEmployees(response.data)
        })
    }, []);


    let [fontsLoaded] = useFonts({
        Roboto_400Regular,
        Roboto_500Medium
    });

    if (!fontsLoaded) {
        return null;
    }
    return (
        <View>
            <FlatList
                data={employees}
                keyExtractor={element => element.name}
                renderItem={({ item }) => {
                    return (
                        <Image
                            source={{
                                uri: item.image,
                            }}
                        />
                    );
                }}
            />
        </View>
    );
};

<FlatList
    data={employees}
    keyExtractor={element => element.name}
    renderItem={({ item }) => {
        return (
            <Image
                source={{
                    uri: item.image,
                }}
            />
        );
    }}
/>

function showEmployee(item) {

    const { name, job, admission_date, image } = item.item

    return (
        <View >
            <View style={styles.witheBox2}>
                {/* <Image
                    source={{ uri: image }}
                    style={{ width: 64, height: 64, borderRadius: 32, margin: 10 }}
                /> */}
                <Text style={{ fontFamily: 'Roboto_400Regular' }}>V</Text>
                <Text style={{ fontFamily: 'Roboto_400Regular', fontSize: 16 }}>{name}</Text>
                <Text style={{ fontFamily: 'Roboto_400Regular' }}>V</Text>
            </View>
        </View>
    )
}


export default Flatlist;
