import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, Image, TouchableOpacity, Button } from 'react-native';
import { useFonts, Roboto_400Regular, Roboto_500Medium } from '@expo-google-fonts/roboto';

import styles from './styles';
import api from '../../services/api';

import axios from 'axios';

const baseUrl = 'http://localhost:3000';

const Flatlist = () => {
    const [employees, setEmployees] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [hasError, setErrorFlag] = useState(false);

    useEffect(() => {
        const source = axios.CancelToken.source();
        const url = `${baseUrl}/employees`;
        const getEmployees = async () => {
            try {
                setIsLoading(true);
                const response = await axios.get(url, { cancelToken: source.token });
                if (response.status === 200) {
                    setEmployees(response.data);
                    setIsLoading(false);
                    return;
                } else {
                    throw new Error('Falha ao obter lista de funcionários');
                }
            } catch (error) {
                if (axios.isCancel(error)) {
                    console.log('Operação cancelada...');
                } else {
                    setErrorFlag(true);
                    setIsLoading(false);
                }
            }
        };
        getEmployees();
        return () => source.cancel('Operação cancelada...');
    }, []);

    let [fontsLoaded] = useFonts({
        Roboto_400Regular,
        Roboto_500Medium
    });

    if (!fontsLoaded) {
        return null;
    }
    return (
        <View>
            <FlatList
                data={employees}
                keyExtractor={element => element.name}
                ItemSeparatorComponent={itemSeparator}
                renderItem={showEmployee}
            />
        </View>
    );
};




function showEmployee(item) {
    const { name, job, admission_date, image } = item.item;

    return (
        <View >
            <TouchableOpacity>
                <View style={styles.witheBox2}>
                    <Image
                        source={{ uri: image }}
                        style={{ width: 34, height: 34, borderRadius: 32, margin: 10 }}
                    />
                    <Text style={{ fontFamily: 'Roboto_400Regular', fontSize: 16 }}>{name}</Text>
                    <Image
                        source={(require('../../assets/chevron-down.png'))}
                        style={{ width: 34, height: 34, margin: 10 }}
                    />
                </View>
            </TouchableOpacity>
        </View>
    )
}

const itemSeparator = () => {
    return <View style={styles.separator}></View>
}


export default Flatlist;
