import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, Image, TextInput } from 'react-native';
import { useFonts, Roboto_400Regular, Roboto_500Medium } from '@expo-google-fonts/roboto';

import styles from './styles';
import api from '../../services/api';

const Flatlist = () => {
    // const [employees, setEmployees] = useState([]);
    const [filteredDAta, setFilteredData] = useState([]);
    const [masterData, setMasterData] = useState([]);
    const [search, setSearch] = useState("");

    useEffect(() => {
        api.get("/employees").then((response) => {
            console.log(response.data);
            setFilteredData(response.data);
            setMasterData(response.data);
        })
    }, []);

    const searchFilter = (text) => {
        if(text) {
            const newData = masterData.filter((item) => {
                const itemData = item.title ? item.title.toUpperCase() : ''.toUpperCase();
                const textData = text.toUpperCase();
                return itemData.indexOf(textData) > 1;
            });
            setFilteredData(newData);
            setSearch(text);
        } else {
            setFilteredData(masterData);
            setSearch(text);
        }

    }


    let [fontsLoaded] = useFonts({
        Roboto_400Regular,
        Roboto_500Medium
    });

    if (!fontsLoaded) {
        return null;
    }
    return (
        <View>
            <View>
                <TextInput
                    style={styles.inputBox}
                    value={search}
                    onChangeText={(text) => searchFilter(text)}
                    placeholder='Pesquisar                                                      ðŸ”' />
            </View>
            <FlatList
                data={filteredDAta}
                keyExtractor={element => element.name}
                ItemSeparatorComponent={itemSeparator}
                renderItem={showEmployee}
            />
        </View>
    );
};

function showEmployee(item) {
    const { name, job, admission_date, image } = item.item

    return (
        <View >
            <View style={styles.witheBox2}>
                <Image
                    source={{ uri: image }}
                    style={{ width: 34, height: 34, borderRadius: 32, margin: 10 }}
                />
                <Text style={{ fontFamily: 'Roboto_400Regular', fontSize: 16 }}>{name}</Text>
                <Text style={{ fontFamily: 'Roboto_400Regular' }}>V</Text>
            </View>
        </View>
    )
}



const itemSeparator = () => {
    return <View style={styles.separator}></View>
}


export default Flatlist;
