import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, Image, TouchableOpacity, Button } from 'react-native';
import { useFonts, Roboto_400Regular, Roboto_500Medium } from '@expo-google-fonts/roboto';

import styles from './styles';
import api from '../../services/api';

const Flatlist = () => {
    const [employees, setEmployees] = useState([]);
const dados = 
    [
        {
          "id": 1,
          "name": "João",
          "job": "Back-end",
          "admission_date": "2019-12-02T00:00:00.000Z",
          "phone": "5551234567890",
          "image": "https://img.favpng.com/25/7/23/computer-icons-user-profile-avatar-image-png-favpng-LFqDyLRhe3PBXM0sx2LufsGFU.jpg"
        },
        {
          "id": 2,
          "name": "Roberto",
          "job": "Front-end",
          "admission_date": "2020-03-12T00:00:00.000Z",
          "phone": "5550321654789",
          "image": "https://e7.pngegg.com/pngimages/550/997/png-clipart-user-icon-foreigners-avatar-child-face.png"
        },
        {
          "id": 3,
          "name": "Maria",
          "job": "Front-end",
          "admission_date": "2020-03-15T00:00:00.000Z",
          "phone": "5557894561230",
          "image": "https://www.clipartmax.com/png/middle/277-2772117_user-profile-avatar-woman-icon-avatar-png-profile-icon.png"
        },
        {
          "id": 4,
          "name": "Cleber",
          "job": "Back-end",
          "admission_date": "2020-06-01T00:00:00.000Z",
          "phone": "5557410258963",
          "image": "https://img.favpng.com/25/7/23/computer-icons-user-profile-avatar-image-png-favpng-LFqDyLRhe3PBXM0sx2LufsGFU.jpg"
        },
        {
          "id": 5,
          "name": "Giovana",
          "job": "Designer",
          "admission_date": "2020-06-20T00:00:00.000Z",
          "phone": "5553698520147",
          "image": "https://www.clipartmax.com/png/middle/277-2772117_user-profile-avatar-woman-icon-avatar-png-profile-icon.png"
        },
        {
          "id": 6,
          "name": "Mario",
          "job": "Front-end",
          "admission_date": "2020-10-01T00:00:00.000Z",
          "phone": "5551234567890",
          "image": "https://e7.pngegg.com/pngimages/550/997/png-clipart-user-icon-foreigners-avatar-child-face.png"
        },
        {
          "id": 7,
          "name": "Gabriel",
          "job": "Back-end",
          "admission_date": "2021-01-01T00:00:00.000Z",
          "phone": "5551234567890",
          "image": "https://img.favpng.com/25/7/23/computer-icons-user-profile-avatar-image-png-favpng-LFqDyLRhe3PBXM0sx2LufsGFU.jpg"
        },
        {
          "id": 8,
          "name": "Carla",
          "job": "Back-end",
          "admission_date": "2021-03-01T00:00:00.000Z",
          "phone": "5551234567890",
          "image": "https://www.clipartmax.com/png/middle/277-2772117_user-profile-avatar-woman-icon-avatar-png-profile-icon.png"
        },
        {
          "id": 10,
          "name": "Fernanda",
          "job": "Front-end",
          "admission_date": "2021-05-01T00:00:00.000Z",
          "phone": "5551234567890",
          "image": "https://www.clipartmax.com/png/middle/277-2772117_user-profile-avatar-woman-icon-avatar-png-profile-icon.png"
        }
      ]

    useEffect(() => {
        async function fetchData() {
          const response = await api.get("/employees").then((response) => {
            // console.log(response.data);
            setEmployees(response.data);})
        }
        fetchData();
      }, []); 

    let [fontsLoaded] = useFonts({
        Roboto_400Regular,
        Roboto_500Medium
    });

    if (!fontsLoaded) {
        return null;
    }
    return (
        <View>
            <FlatList
                data={dados}
                keyExtractor={element => element.name}
                ItemSeparatorComponent={itemSeparator}
                renderItem={showEmployee}
            />
        </View>
    );
};


function showEmployee(item) {
    const { name, job, admission_date, image } = item.item;

    return (
        <View >
            <TouchableOpacity>
                <View style={styles.witheBox2}>
                    <Image
                        source={{ uri: image }}
                        style={{ width: 34, height: 34, borderRadius: 32, margin: 10 }}
                    />
                    <Text style={{ fontFamily: 'Roboto_400Regular', fontSize: 16 }}>{name}</Text>
                    <Image
                        source={(require('../../assets/chevron-down.png'))}
                        style={{ width: 34, height: 34, margin: 10 }}
                    />
                </View>
            </TouchableOpacity>
        </View>
    )
}

const itemSeparator = () => {
    return <View style={styles.separator}></View>
}


export default Flatlist;
